-- MSSQL 샘플 데이터 삽입 스크립트
-- 테스트용 데이터를 robot_info, inventory_info, pod_info 테이블에 삽입

USE cdc_test;
GO

-- 1. robot_info 테이블에 20개 샘플 데이터 삽입

-- 2. inventory_info 테이블에 10개 샘플 데이터 삽입

-- 3. pod_info 테이블에 10개 샘플 데이터 삽입

-- 데이터 삽입 확인
SELECT 'robot_info' as table_name, COUNT(*) as record_count FROM robot_info
UNION ALL
SELECT 'inventory_info' as table_name, COUNT(*) as record_count FROM inventory_info
UNION ALL
SELECT 'pod_info' as table_name, COUNT(*) as record_count FROM pod_info;

-- 샘플 데이터 확인
SELECT TOP 5 * FROM robot_info ORDER BY report_time DESC;
SELECT TOP 5 * FROM inventory_info ORDER BY report_time DESC;
SELECT TOP 5 * FROM pod_info ORDER BY report_time DESC; 